{% set is_overdue = frappe.utils.getdate(frappe.utils.nowdate()) > doc.due_date %}
<!DOCTYPE html>
<html>

<head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Reminder: Submit Your Report</title>

    <style>
        body {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
            line-height: 1.6;
            margin: 0;
            padding: 0;
            background-color: #f5f5f5;
            color: #333;
        }

        .container-noti {
            max-width: 600px;
            margin: 0 auto;
            padding: 20px;
            background-color: white;
            border-radius: 12px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }

        .header-noti {
            text-align: center;
            padding: 25px 0;
            border-bottom: 1px solid #eee;
            background: linear-gradient(to right, #f8f9fa, #fff);
            border-radius: 12px 12px 0 0;
        }

        .logo-noti {
            width: 100px;
            height: 100px;
            margin: 0 auto 15px;
        }

        .logo-noti img {
            width: 100%;
            height: 100%;
            object-fit: contain;
        }

        .content-noti {
            padding: 30px 25px;
        }

        .button-noti {
            display: inline-block;
            padding: 14px 28px;
            background-color: #8B1818;
            color: white;
            text-decoration: none;
            border-radius: 8px;
            margin: 20px 0;
            text-align: center;
            font-weight: 500;
            transition: background-color 0.3s ease;
        }

        .section {
            margin: 25px 0;
            padding: 20px;
            background-color: #f8f9fa;
            border-radius: 8px;
            border-left: 4px solid #8B1818;
        }

        .section-title {
            color: #8B1818;
            font-weight: 600;
            margin-bottom: 15px;
            font-size: 1.1em;
        }

        .expired-warning {
            background-color: #ffdddd;
            border-left: 4px solid #d9534f;
            padding: 15px;
            margin: 20px 0;
            border-radius: 8px;
            color: #a94442;
            font-weight: bold;
            text-align: center;
        }

        .footer-noti {
            text-align: center;
            padding: 25px;
            color: #666;
            font-size: 14px;
            border-top: 1px solid #eee;
            background: linear-gradient(to right, #f8f9fa, #fff);
            border-radius: 0 0 12px 12px;
        }
    </style>
</head>

<body>

    <div class="container-noti">
        <div class="header-noti">
            <div class="logo-noti">
                <img src="{{client_logo()}}" alt="Grant Logo">
            </div>
            <h1 style="color: #333; margin: 15px 0 5px;">Reminder: Submit Your Report</h1>
            <p style="color: #666;">Grant Name: {{ doc.grant_name }}</p>
        </div>

        <div class="content-noti">
            <h2 style="color: #8B1818; margin-bottom: 25px;">Hello {{ doc.ngo_name or "--" }},</h2>

            {% if is_overdue %}
                <div class="expired-warning">
                    ⚠️ Your report submission is overdue! Please submit it immediately.
                </div>
            {% endif %}

            <p>Please submit your report as per the reporting schedule.</p>

            <div class="section">
                <div class="section-title">Report Details</div>
                <p><strong>Report Name:</strong> {{ doc.report_name }}</p>
                <p><strong>Report ID:</strong> {{ doc.name }}</p>
                <p><strong>Report Type:</strong> {{ doc.report_type }}</p>
                <p><strong>Submission Status:</strong> {{ doc.submission_status }}</p>
                <p><strong>Due Date:</strong> {{ frappe.utils.format_date(doc.due_date, "dd-MM-yyyy") }}</p>
            </div>

            <center><a href="/app/grant/{{doc.grant}}#reportings_tab" class="button-noti">Submit Report</a></center>

            <p style="margin-top: 30px; text-align: center;">Best regards,<br>The {{doc.donor_name}} mGrant Team</p>

        </div>

        <div class="footer-noti">
            <p>© {{ frappe.utils.getdate().year }} {{doc.donor_name}}. All rights reserved.</p>
            <p>For support, contact: <a href="mailto:{{doc.donor_email}}">{{doc.donor_email}}</a</p>
        </div>

    </div>
</body>
</html>

