        }
        .header-noti {
            text-align: center;
            padding: 20px 0;
            border-bottom: 1px solid #eee;
        }
        .logo-noti {
            width: 100px;
            height: 100px;
            margin: 0 auto;
        }
        .logo-noti img {
            width: 100%;
            height: 100%;
            object-fit: contain;
        }
        .content-noti {
            padding: 30px 20px;
            color: #333;
        }
        .button-noti {
            display: inline-block;
            padding: 12px 24px;
            background-color: #8B1818;
            color: white;
            text-decoration: none;
            border-radius: 4px;
            margin: 20px 0;
            text-align: center;
            width: 200px;
        }
        .footer-noti {
            text-align: center;
            padding: 20px;
            color: #666;
            font-size: 14px;
            border-top: 1px solid #eee;
        }
        .urgent-reminder-box {
            background-color: #fff3cd;
            padding: 15px;
            border-radius: 4px;
            margin: 20px 0;
            border-left: 4px solid #ffc107;
        }
        .action-box {
            background-color: #d1ecf1;
            padding: 15px;
            border-radius: 4px;
            margin: 20px 0;
            border-left: 4px solid #17a2b8;
        }
    </style>
